mapper.select:
SELECT l.*, u.username
FROM operation_log l
LEFT JOIN user u ON l.user_id = u.id


        AND l.module = #{module}


        AND l.operation = #{operation}


        AND u.username LIKE CONCAT('%', #{username}, '%')


        AND l.create_time >= #{startDate} 


        AND l.create_time <= #{endDate} 


ORDER BY l.create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM operation_log l
LEFT JOIN user u ON l.user_id = u.id


        AND l.module = #{module}


        AND l.operation = #{operation}


        AND u.username LIKE CONCAT('%', #{username}, '%')


        AND l.create_time >= #{startDate} 


        AND l.create_time <= #{endDate}

mapper.insert:
INSERT INTO operation_log (
    user_id, module, operation, target_id, target_name,
    ip_address, user_agent, before_data, after_data, result, error_msg
) VALUES (
    #{userId}, #{module}, #{operation}, #{targetId}, #{targetName},
    #{ipAddress}, #{userAgent}, #{beforeData}, #{afterData}, #{result}, #{errorMsg}
)
